INSERT INTO `VALORE_CC` (`ID_VALORE`, `DESCRIZIONE`) VALUES
(1, 'Manifesta mai o raramente i comportamenti descritti, mostra mancanza di aderenza ai comportamenti attesi o richiesti'),
